  functionName: 'my-lambda',
  tableName: 'my-table',
  documentName: 'my-configuration',
  cmk: 'alias/my-key-for-lambda'
})

const data = 'HI, This is my secret';
config1.set(data, 'additionField').then(() => {
  console.log('done');
});

